mapper.select:

  		   SELECT BKUP_ID			/*백업ID */
			    , PRJT_ID			/*프로젝트ID  */
				, PGM_ID			/*프로그램ID  */
				, BZCD				/*업무구분 */
				, PGM_NM			/*프로그램명  */
				, BZ_DTLS_TXT       /*업무세부 */
				, DVLP_DIS_CD       /*개발구분*/
				, PGM_DIS_CD        /*프로그램구분*/
				, ENLPE_NM			/*프로그램세부구분코드 */
				, FRCS_STA_DT		/*예상시작일자 */
				, FRCS_END_DT		/*예상종료일자 */
				, STA_DT			/*실제시작일자 */
				, END_DT			/*실제종료일자 */
				, PRG_TXT			/*진행현황 */
				, DVLPE_NO			/*개발자번호 */
				, DVLPE_CNF_DT		/*개발자확인일자 */
				, PL_NO				/*PL번호 */
				, PL_CNF_DT			/*PL확인일자 */
				, CRPE_NO			/*담당자번호 */
				, RMRK				/*비고 */
				, OPR_NO			/*조작자번호 */
				, DB_CHG_TS			/*DB변경일시 */ 
  		     FROM PMS_PGM_MNG

            WHERE 1=1
	   		 /*프로그램ID*/

	   		   AND PGM_ID = '%' #{pgm_id} '%'

	   		 /*프로그램명*/

	   		   AND PGM_NM LIKE '%' #{pgm_nm} '%'

	   		 /*개발자번호*/

	   		   AND DVLPE_NO = #{dvlpe_no} 

	   		 /*PL번호*/

	   		   AND PL_NO = #{pl_no}

	   		 /*개발구분*/

	   		   AND DVLP_DIS_CD = #{dvlp_dis_cd}

	   		 /*업무구분*/

	   		   AND BZCD = #{bzcd}

	   		 /*프로그램구분*/

	   		   AND PGM_DIS_CD = #{pgm_dis_cd}
