mapper.select:
SELECT A.BZCD /* 업무구분 */ , A.BZ_DTLS_TXT /* 업무세부 */ , A.PGM_ID /* 프로그램ID */ , A.PGM_NM /* 프로그램명 */ , A.DVLP_DIS_CD /* 개발구분 */ , A.PGM_DIS_CD /* 프로그램구분 */ , A.ENLPE_NM /* 프로그램세부구분코드 */ , NVL2(A.FRCS_STA_DT,STR_TO_DATE(A.FRCS_STA_DT , '%Y%m%d'),'') AS FRCS_STA_DT /* 예상시작일자 */ , NVL2(A.FRCS_END_DT,STR_TO_DATE(A.FRCS_END_DT , '%Y%m%d'),'') AS FRCS_END_DT /* 예상종료일자 */ , CASE WHEN A.STA_DT IS NOT NULL AND A.STA_DT != '' THEN STR_TO_DATE(A.STA_DT, '%Y%m%d') ELSE A.STA_DT END AS STA_DT /* 실제시작일자 */ , CASE WHEN A.END_DT IS NOT NULL AND A.END_DT != '' THEN STR_TO_DATE(A.END_DT, '%Y%m%d') ELSE A.END_DT END AS END_DT /* 실제종료일자 */ , CASE WHEN A.DVLPE_CNF_DT IS NOT NULL AND A.DVLPE_CNF_DT != '' THEN STR_TO_DATE(A.DVLPE_CNF_DT, '%Y%m%d') ELSE A.DVLPE_CNF_DT END AS DVLPE_CNF_DT /* 개발자확인일자 */ , CASE WHEN A.PL_CNF_DT IS NOT NULL AND A.PL_CNF_DT != '' THEN STR_TO_DATE(A.PL_CNF_DT, '%Y%m%d') ELSE A.PL_CNF_DT END AS PL_CNF_DT /* PL확인일자 */ , A.PRC_STEP_CD /* 처리단계 */ , A.PRG_TXT /* 진행현황 */ , B.EMPNM AS DVLPE_NM /* 개발명 */ , C.EMPNM AS PL_NM /* PL명 */ , D.EMPNM AS CRPE_NM /* 담당자명 */ , ( SELECT COUNT(*) FROM pms_err_mng WHERE CCTN_ID = A.PGM_ID AND CCTN_BZCD = A.BZCD AND PRJT_ID = A.PRJT_ID AND BKUP_ID = A.BKUP_ID ) AS ERR_TOT_CNT /* 전체 결함등록 건수 */ , ( SELECT COUNT(*) FROM pms_err_mng WHERE ERR_PRC_STEP_CD IN ('600', '700', '800') AND CCTN_ID = A.PGM_ID AND CCTN_BZCD = A.BZCD AND PRJT_ID = A.PRJT_ID AND BKUP_ID = A.BKUP_ID ) AS ERR_CMPL_CNT /* 결함완료 건수 */ , ( SELECT COUNT(*) FROM pms_err_mng WHERE ERR_PRC_STEP_CD IN ('100', '200', '300', '400', '500') AND CCTN_ID = A.PGM_ID AND CCTN_BZCD = A.BZCD AND PRJT_ID = A.PRJT_ID AND BKUP_ID = A.BKUP_ID ) AS ERR_NCMPL_CNT /* 결함진행 건수 */ , A.ATFL_MNG_ID /* 단위테스트결과서 첨부파일관리ID*/ , A.PAL_ATFL_MNG_ID /* 설계서 첨부파일관리ID*/ , A.RMRK /* 비고 */ , A.DVLPE_NO /* 개발자번호 */ , A.PL_NO /* PL번호 */ , A.CRPE_NO /* 담당자번호 */ , 'Y' AS SAVE_YN /* 등록여부*/ , A.BKUP_ID /* 백업ID */ , A.PRJT_ID /* 프로젝트ID */ FROM pms_pgm_mng A /* PMS프로그램관리 */ LEFT OUTER JOIN pms_emp_info B ON B.EMPNO = A.DVLPE_NO AND B.PRJT_ID = A.PRJT_ID AND B.BKUP_ID = A.BKUP_ID LEFT OUTER JOIN pms_emp_info C ON C.EMPNO = A.PL_NO AND C.PRJT_ID = A.PRJT_ID AND C.BKUP_ID = A.BKUP_ID LEFT OUTER JOIN pms_emp_info D ON D.EMPNO = A.CRPE_NO AND D.PRJT_ID = A.PRJT_ID AND D.BKUP_ID = A.BKUP_ID WHERE 1=1 /*백업ID*/
AND A.BKUP_ID LIKE '%' #{bkup_id} '%'
/*프로젝트ID*/
AND A.PRJT_ID LIKE '%' #{prjt_id} '%'
/*업무구분*/
AND A.BZCD LIKE '%' #{bzcd} '%'
/*개발구분*/
AND A.DVLP_DIS_CD = #{dvlp_dis_cd}
/*프로그램구분*/
AND A.PGM_DIS_CD = #{pgm_dis_cd}
/*처리구분*/
AND A.PRC_STEP_CD = #{prc_step_cd}
/* 예상종료일자 */
AND A.FRCS_END_DT BETWEEN REPLACE(#{frcs_sta_dt}, '-', '') AND REPLACE(#{frcs_end_dt}, '-', '')
/* 개발자종료일자 */
AND A.DVLPE_CNF_DT BETWEEN REPLACE(#{dvlpe_sta_dt}, '-', '') AND REPLACE(#{dvlpe_end_dt}, '-', '')
/*프로그램ID*/
AND A.PGM_ID LIKE '%' #{pgm_id} '%'
/*프로그램명*/
AND A.PGM_NM LIKE '%' #{pgm_nm} '%'
/*개발자번호*/
AND A.DVLPE_NO = #{dvlpe_no}
/*PL번호*/
AND A.PL_NO = #{pl_no}
ORDER BY A.BZCD, A.FRCS_STA_DT

mapper.update:

UPDATE pms_pgm_mng
   SET ATFL_MNG_ID  = #{atfl_mng_id}  /*첨부파일관리ID*/
     , OPR_NO       = #{empno}
     , DVLP_DIS_CD  = '400'
     , DB_CHG_TS    = DATE_FORMAT(NOW(),'%Y%m%d')
WHERE PGM_ID        = #{pgm_id}
   AND BZCD         = #{bzcd}
   AND PRJT_ID      = #{prjt_id}
   AND BKUP_ID      = #{bkup_id}
