SELECT A.BZCD AS BZCD , A.BZNM AS BZNM , A.SQN_CD AS SQN_CD , A.SQN_NM AS SQN_NM , NVL(B.TOT_CNT,0) AS TOT_CNT , NVL(B.AF_TOT_CNT,0) AS AF_TOT_CNT , NVL(B.CMPL_CNT,0) AS CMPL_CNT , NVL(B.AF_CMPL_CNT,0) AS AF_CMPL_CNT , NVL(B.NCMPL_CNT,0) AS NCMPL_CNT , NVL(B.PRNR_RT,0) AS PRNR_RT , NVL(C.TOT_CNT,0) AS TOT_CNT1 , NVL(C.CMPL_CNT,0) AS CMPL_CNT1 , NVL(C.NCMPL_CNT,0) AS NCMPL_CNT1 , NVL(C.PRNR_RT,0) AS PRNR_RT1 , NVL(D.TOT_CNT,0) AS PL_TOT_CNT , NVL(D.CMPL_CNT,0) AS PL_CMPL_CNT , NVL(D.NCMPL_CNT,0) AS PL_NCMPL_CNT , NVL(D.PRNR_RT,0) AS PL_PRNR_RT , NVL(F.TOT_CNT,0) AS TOT_CNT2 , NVL(F.CMPL_CNT,0) AS CMPL_CNT2 FROM (SELECT A.DTLS_TYCD AS BZCD , A.DTLS_TYNM AS BZNM , NVL(B.SQN_CD,'100') AS SQN_CD , NVL(C.SQN_NM,'1차수') AS SQN_NM FROM pms_cd_del_mng A LEFT OUTER JOIN (SELECT BZCD ,SQN_CD FROM pms_pgm_mng WHERE SQN_CD = CASE WHEN #{sqn_cd} = 'TTT' THEN SQN_CD ELSE #{sqn_cd} END AND PRJT_ID = #{prjt_id} AND BKUP_ID = #{bkup_id} GROUP BY BZCD, SQN_CD) B ON B.BZCD = A.DTLS_TYCD LEFT OUTER JOIN (SELECT DTLS_TYCD AS SQN_CD ,DTLS_TYNM AS SQN_NM FROM pms_cd_del_mng WHERE USE_YN = 'Y' AND GRP_TYCD = '1000000006' AND PRJT_ID = #{prjt_id} AND BKUP_ID = #{bkup_id}) C ON B.SQN_CD = C.SQN_CD WHERE USE_YN = 'Y' AND ETC_TXT1 = 'Y' AND GRP_TYCD = '1000000001' AND PRJT_ID = #{prjt_id} AND BKUP_ID = #{bkup_id}) A INNER JOIN (SELECT AA.BZCD , AA.SQN_CD , AA.TOT_CNT , AA.AF_TOT_CNT , AA.CMPL_CNT , AA.AF_CMPL_CNT , AA.NCMPL_CNT , NVL2(AA.TOT_CNT,ROUND((((AA.CMPL_CNT + AA.AF_CMPL_CNT)/AA.TOT_CNT) * 100), 2),0) AS PRNR_RT FROM (SELECT A.BZCD , A.SQN_CD , COUNT(*) AS TOT_CNT , SUM(CASE WHEN A.FRCS_END_DT <= DATE_FORMAT(#{inp_date},'%Y%m%d') THEN 1 ELSE 0 END ) AS AF_TOT_CNT , SUM(CASE WHEN A.FRCS_END_DT <= DATE_FORMAT(#{inp_date},'%Y%m%d')AND A.PRC_STEP_CD IN ('200', '300', '400','500','600') THEN 1 ELSE 0 END ) AS CMPL_CNT , SUM(CASE WHEN A.FRCS_END_DT > DATE_FORMAT(#{inp_date},'%Y%m%d') AND A.PRC_STEP_CD IN ('200', '300', '400','500','600') THEN 1 ELSE 0 END ) AS AF_CMPL_CNT , SUM(CASE WHEN A.FRCS_END_DT <= DATE_FORMAT(#{inp_date},'%Y%m%d') AND A.PRC_STEP_CD IN ('000', '100','110') THEN 1 ELSE 0 END ) AS NCMPL_CNT FROM pms_pgm_mng A WHERE 1=1 AND A.DVLP_DIS_CD IN (SELECT DTLS_TYCD FROM pms_cd_del_mng WHERE BKUP_ID = A.BKUP_ID AND PRJT_ID = A.PRJT_ID AND GRP_TYCD = '1000000031' AND USE_YN = 'Y') AND A.SQN_CD = CASE WHEN #{sqn_cd} = 'TTT' THEN A.SQN_CD ELSE #{sqn_cd} END AND A.PRJT_ID = #{prjt_id} AND A.BKUP_ID = #{bkup_id} GROUP BY A.BZCD, A.SQN_CD ) AA ) B ON B.BZCD = A.BZCD AND B.SQN_CD = A.SQN_CD LEFT OUTER JOIN (SELECT BB.BZCD , BB.SQN_CD , BB.TOT_CNT , BB.CMPL_CNT , BB.NCMPL_CNT , NVL2(BB.TOT_CNT,ROUND(((BB.CMPL_CNT/BB.TOT_CNT) * 100), 2),0) AS PRNR_RT FROM (SELECT A.BZCD , A.SQN_CD , COUNT(*) AS TOT_CNT , SUM(CASE WHEN A.PRC_STEP_CD IN ('200', '300', '400','500','600') THEN 1 ELSE 0 END ) AS CMPL_CNT , SUM(CASE WHEN A.PRC_STEP_CD IN ('000', '100','110') THEN 1 ELSE 0 END ) AS NCMPL_CNT FROM pms_pgm_mng A WHERE 1=1 AND A.DVLP_DIS_CD IN (SELECT DTLS_TYCD FROM pms_cd_del_mng WHERE BKUP_ID = A.BKUP_ID AND PRJT_ID = A.PRJT_ID AND GRP_TYCD = '1000000031' AND USE_YN = 'Y') AND A.FRCS_END_DT BETWEEN DATE_FORMAT(DATE_ADD(#{inp_date},INTERVAL -6 DAY),'%Y%m%d') AND DATE_FORMAT(#{inp_date},'%Y%m%d') AND A.SQN_CD = CASE WHEN #{sqn_cd} = 'TTT' THEN A.SQN_CD ELSE #{sqn_cd} END AND A.PRJT_ID = #{prjt_id} AND A.BKUP_ID = #{bkup_id} GROUP BY A.BZCD, A.SQN_CD ) BB ) C ON C.BZCD = A.BZCD AND C.SQN_CD = A.SQN_CD LEFT OUTER JOIN (SELECT CC.BZCD , CC.SQN_CD , CC.TOT_CNT , CC.CMPL_CNT , CC.NCMPL_CNT , NVL2(CC.TOT_CNT,ROUND(((CC.CMPL_CNT/CC.TOT_CNT) * 100), 2),0) AS PRNR_RT FROM (SELECT BZCD , SQN_CD , COUNT(*) AS TOT_CNT , SUM(CASE WHEN A.PRC_STEP_CD IN ('300', '400','500','600') THEN 1 ELSE 0 END ) AS CMPL_CNT , SUM(CASE WHEN A.PRC_STEP_CD IN ('000', '100','110','200') THEN 1 ELSE 0 END ) AS NCMPL_CNT FROM pms_pgm_mng A WHERE 1=1 AND A.DVLP_DIS_CD IN (SELECT DTLS_TYCD FROM pms_cd_del_mng WHERE BKUP_ID = A.BKUP_ID AND PRJT_ID = A.PRJT_ID AND GRP_TYCD = '1000000031' AND USE_YN = 'Y') AND DATE_FORMAT(DATE_ADD(A.DVLPE_CNF_DT,INTERVAL CAST(#{proc_days} AS INT) DAY),'%Y%m%d') BETWEEN DATE_FORMAT(DATE_ADD(#{inp_date},INTERVAL -6 DAY),'%Y%m%d') AND DATE_FORMAT(#{inp_date},'%Y%m%d') AND A.SQN_CD = CASE WHEN #{sqn_cd} = 'TTT' THEN A.SQN_CD ELSE #{sqn_cd} END AND A.PRJT_ID = #{prjt_id} AND A.BKUP_ID = #{bkup_id} GROUP BY A.BZCD , A.SQN_CD ) CC ) D ON D.BZCD = A.BZCD AND D.SQN_CD = A.SQN_CD LEFT OUTER JOIN (SELECT DD.BZCD , DD.SQN_CD , DD.TOT_CNT , DD.CMPL_CNT FROM (SELECT BZCD , SQN_CD , COUNT(*) AS TOT_CNT , SUM(CASE WHEN A.PRC_STEP_CD IN ('200','300', '400','500','600') THEN 1 ELSE 0 END ) AS CMPL_CNT FROM pms_pgm_mng A WHERE 1=1 AND A.DVLP_DIS_CD IN (SELECT DTLS_TYCD FROM pms_cd_del_mng WHERE BKUP_ID = A.BKUP_ID AND PRJT_ID = A.PRJT_ID AND GRP_TYCD = '1000000031' AND USE_YN = 'Y') AND A.FRCS_END_DT BETWEEN DATE_FORMAT(DATE_ADD(#{inp_date},INTERVAL 1 DAY),'%Y%m%d') AND DATE_FORMAT(DATE_ADD(#{inp_date},INTERVAL 7 DAY),'%Y%m%d') AND A.SQN_CD = CASE WHEN #{sqn_cd} = 'TTT' THEN A.SQN_CD ELSE #{sqn_cd} END AND A.PRJT_ID = #{prjt_id} AND A.BKUP_ID = #{bkup_id} GROUP BY A.BZCD, A.SQN_CD ) DD ) F ON F.BZCD = A.BZCD AND F.SQN_CD = A.SQN_CD
WHERE 1=1 AND A.BZCD = #{bzcd}
ORDER BY A.SQN_CD DESC, A.BZCD

SELECT A.BZCD AS BZCD , A.BZNM AS BZNM , A.SQN_CD AS SQN_CD , A.SQN_NM AS SQN_NM , CASE WHEN COUNT(*) = 1 AND B.ERR_TYCD IS NULL THEN 0 ELSE COUNT(*) END AS TOT_ERR_CNT , SUM(CASE WHEN B.ERR_TYCD = '100' THEN 1 ELSE 0 END ) AS ERR_CNT , SUM(CASE WHEN B.ERR_TYCD = '200' THEN 1 ELSE 0 END) AS IMPT_CNT , SUM(CASE WHEN B.ERR_TYCD = '900' THEN 1 ELSE 0 END) AS ETC_ERR_CNT , SUM(CASE WHEN B.ERR_TYCD = '300' THEN 1 ELSE 0 END) AS NERR_CNT , SUM(CASE WHEN B.ERR_TYCD = '300' OR B.ERR_PRC_STEP_CD IN (SELECT DTLS_TYCD FROM pms_cd_del_mng WHERE BKUP_ID = A.BKUP_ID AND PRJT_ID = A.PRJT_ID AND GRP_TYCD = '1000000034' AND USE_YN = 'Y') THEN 1 ELSE 0 END) AS CMPL_CNT , SUM(CASE WHEN B.ERR_TYCD != '300' AND B.ERR_PRC_STEP_CD != '500' AND B.ERR_PRC_STEP_CD NOT IN (SELECT DTLS_TYCD FROM pms_cd_del_mng WHERE BKUP_ID = A.BKUP_ID AND PRJT_ID = A.PRJT_ID AND GRP_TYCD = '1000000034' AND USE_YN = 'Y') THEN 1 ELSE 0 END) AS NCMPL_CNT , SUM(CASE WHEN B.ERR_PRC_STEP_CD = '500' THEN 1 ELSE 0 END) AS SPND_CNT FROM (SELECT A.DTLS_TYCD AS BZCD ,A.DTLS_TYNM AS BZNM ,NVL(B.SQN_CD,'100') AS SQN_CD ,NVL(C.SQN_NM,'1차수') AS SQN_NM ,B.PRJT_ID AS PRJT_ID ,B.BKUP_ID AS BKUP_ID FROM pms_cd_del_mng A LEFT OUTER JOIN (SELECT BZCD , SQN_CD , PRJT_ID , BKUP_ID FROM pms_pgm_mng WHERE SQN_CD = CASE WHEN #{sqn_cd} = 'TTT' THEN SQN_CD ELSE #{sqn_cd} END AND PRJT_ID = #{prjt_id} AND BKUP_ID = #{bkup_id} GROUP BY BZCD, SQN_CD) B ON B.BZCD = A.DTLS_TYCD LEFT OUTER JOIN (SELECT DTLS_TYCD AS SQN_CD ,DTLS_TYNM AS SQN_NM FROM pms_cd_del_mng WHERE GRP_TYCD = '1000000006' AND PRJT_ID = #{prjt_id} AND BKUP_ID = #{bkup_id}) C ON B.SQN_CD = C.SQN_CD WHERE A.GRP_TYCD = '1000000001' AND A.PRJT_ID = #{prjt_id} AND A.BKUP_ID = #{bkup_id} ) A LEFT OUTER JOIN pms_err_mng B ON B.RGS_DSCD = '1100' AND B.CCTN_BZCD = A.BZCD AND B.CCTN_SQN_CD = A.SQN_CD AND B.ERR_TYCD NOT LIKE '8%' AND B.PRJT_ID = #{prjt_id} AND B.BKUP_ID = #{bkup_id} WHERE 1=1 AND B.PRJT_ID = #{prjt_id} AND B.BKUP_ID = #{bkup_id} AND B.CCTN_SQN_CD = CASE WHEN #{sqn_cd} = 'TTT' THEN CCTN_SQN_CD ELSE #{sqn_cd} END
AND A.BZCD = #{bzcd}
GROUP BY A.BZCD, A.SQN_CD, A.SQN_NM, A.BZNM ORDER BY A.SQN_CD DESC, A.BZCD, A.SQN_CD
