mapper.select:
/* 담당자별 통합테스트현황 */ SELECT A.BZCD AS BZCD , A.BZNM AS BZNM , A.SQN_CD AS SQN_CD , A.SQN_NM AS SQN_NM , CASE WHEN B.EMPNM IS NULL THEN '미배정' ELSE B.EMPNM END AS EMP_NM , A.TOT_CNT AS TOT_CNT , A.CMPL_CNT AS CMPL_CNT , A.NCMPL_CNT AS NCMPL_CNT , A.PRNR_RT AS PRNR_RT , A.TOT_CNT1 AS TOT_CNT1 , A.CMPL_CNT1 AS CMPL_CNT1 , A.NCMPL_CNT1 AS NCMPL_CNT1 , A.PRNR_RT1 AS PRNR_RT1 , A.TOT_CNT2 AS TOT_CNT2 , A.CMPL_CNT2 AS CMPL_CNT2 FROM (SELECT A.BZCD AS BZCD , A.BZNM AS BZNM , A.SQN_CD AS SQN_CD , A.SQN_NM AS SQN_NM , A.DVLPE_ENO AS DVLPE_ENO , A.PRJT_ID AS PRJT_ID , A.BKUP_ID AS BKUP_ID , B.TOT_CNT AS TOT_CNT , B.CMPL_CNT AS CMPL_CNT , B.NCMPL_CNT AS NCMPL_CNT , B.PRNR_RT AS PRNR_RT , C.TOT_CNT AS TOT_CNT1 , C.CMPL_CNT AS CMPL_CNT1 , C.NCMPL_CNT AS NCMPL_CNT1 , C.PRNR_RT AS PRNR_RT1 , D.TOT_CNT AS TOT_CNT2 , D.CMPL_CNT AS CMPL_CNT2 FROM (SELECT A.DTLS_TYCD AS BZCD , A.DTLS_TYNM AS BZNM , A.PRJT_ID AS PRJT_ID , A.BKUP_ID AS BKUP_ID , B.DVLPE_ENO AS DVLPE_ENO , B.SQN_CD AS SQN_CD , NVL(C.SQN_NM,'1차수') AS SQN_NM FROM pms_cd_del_mng A LEFT OUTER JOIN (SELECT BZCD ,DVLPE_ENO ,SQN_CD FROM pms_itg_tst_mng WHERE SQN_CD = CASE WHEN #{sqn_cd} = 'TTT' THEN SQN_CD ELSE #{sqn_cd} END AND PRJT_ID = #{prjt_id} AND BKUP_ID = #{bkup_id} GROUP BY BZCD, DVLPE_ENO, SQN_CD) B ON B.BZCD = A.DTLS_TYCD LEFT OUTER JOIN (SELECT DTLS_TYCD AS SQN_CD ,DTLS_TYNM AS SQN_NM FROM pms_cd_del_mng WHERE GRP_TYCD = '1000000006' AND PRJT_ID = #{prjt_id} AND BKUP_ID = #{bkup_id}) C ON B.SQN_CD = C.SQN_CD WHERE GRP_TYCD = '1000000001' AND PRJT_ID = #{prjt_id} AND BKUP_ID = #{bkup_id} ORDER BY A.DTLS_TYCD , A.DTLS_TYNM , B.SQN_CD, B.DVLPE_ENO ) A LEFT OUTER JOIN (SELECT AA.BZCD , AA.DVLPE_ENO , AA.SQN_CD , AA.TOT_CNT , AA.CMPL_CNT , AA.NCMPL_CNT , NVL2(AA.TOT_CNT,ROUND(((AA.CMPL_CNT/AA.TOT_CNT) * 100), 2),0) AS PRNR_RT FROM (SELECT A.BZCD , A.SQN_CD , A.DVLPE_ENO , COUNT(*) AS TOT_CNT , SUM(CASE WHEN A.ITG_TST_PRC_CD IN ('200','300', '400','500','600') THEN 1 ELSE 0 END ) AS CMPL_CNT , SUM(CASE WHEN A.FRCS_END_DT < #{inp_date} AND A.ITG_TST_PRC_CD IN ('000', '100') THEN 1 ELSE 0 END ) AS NCMPL_CNT FROM pms_itg_tst_mng A WHERE 1=1 AND A.SQN_CD = CASE WHEN #{sqn_cd} = 'TTT' THEN SQN_CD ELSE #{sqn_cd} END AND A.PRJT_ID = #{prjt_id} AND A.BKUP_ID = #{bkup_id} GROUP BY A.BZCD, A.SQN_CD, A.DVLPE_ENO ) AA ) B ON B.BZCD = A.BZCD AND B.SQN_CD = A.SQN_CD AND B.DVLPE_ENO = A.DVLPE_ENO LEFT OUTER JOIN (SELECT BB.BZCD , BB.DVLPE_ENO , BB.SQN_CD , BB.TOT_CNT , BB.CMPL_CNT , BB.NCMPL_CNT , NVL2(BB.TOT_CNT,ROUND(((BB.CMPL_CNT/BB.TOT_CNT) * 100), 2),0) AS PRNR_RT FROM (SELECT A.BZCD , A.SQN_CD , A.DVLPE_ENO , COUNT(*) AS TOT_CNT , SUM(CASE WHEN A.ITG_TST_PRC_CD IN ('200','300', '400','500','600') THEN 1 ELSE 0 END ) AS CMPL_CNT , SUM(CASE WHEN A.ITG_TST_PRC_CD IN ('000', '100') THEN 1 ELSE 0 END ) AS NCMPL_CNT FROM pms_itg_tst_mng A WHERE 1=1 AND A.SQN_CD = CASE WHEN #{sqn_cd} = 'TTT' THEN SQN_CD ELSE #{sqn_cd} END AND A.PRJT_ID = #{prjt_id} AND A.BKUP_ID = #{bkup_id} AND A.FRCS_END_DT BETWEEN DATE_FORMAT(DATE_ADD(#{inp_date},INTERVAL -6 DAY),'%Y%m%d') AND #{inp_date} GROUP BY A.BZCD, A.SQN_CD, A.DVLPE_ENO ) BB ) C ON C.BZCD = A.BZCD AND C.SQN_CD = A.SQN_CD AND C.DVLPE_ENO = A.DVLPE_ENO LEFT OUTER JOIN (SELECT CC.BZCD , CC.SQN_CD , CC.DVLPE_ENO , CC.TOT_CNT , CC.CMPL_CNT FROM (SELECT A.BZCD , A.SQN_CD , A.DVLPE_ENO , COUNT(*) AS TOT_CNT , SUM(CASE WHEN A.ITG_TST_PRC_CD IN ('200','300', '400','500','600') THEN 1 ELSE 0 END ) AS CMPL_CNT FROM pms_itg_tst_mng A WHERE 1=1 AND A.SQN_CD = CASE WHEN #{sqn_cd} = 'TTT' THEN SQN_CD ELSE #{sqn_cd} END AND A.PRJT_ID = #{prjt_id} AND A.BKUP_ID = #{bkup_id} AND A.FRCS_END_DT BETWEEN DATE_FORMAT(DATE_ADD(#{inp_date},INTERVAL 1 DAY),'%Y%m%d') AND DATE_FORMAT(DATE_ADD(#{inp_date},INTERVAL 7 DAY),'%Y%m%d') GROUP BY A.BZCD, A.SQN_CD, A.DVLPE_ENO ) CC ) D ON D.BZCD = A.BZCD AND D.SQN_CD = A.SQN_CD AND D.DVLPE_ENO = A.DVLPE_ENO WHERE 1=1
AND A.BZCD = #{bzcd} ) A
) A
LEFT OUTER JOIN pms_emp_info B ON B.EMPNO = A.DVLPE_ENO AND B.PRJT_ID = A.PRJT_ID AND B.BKUP_ID = A.BKUP_ID ORDER BY A.BZCD, A.SQN_CD, B.EMPNM
