mapper.select:
  SELECT
		  BZCD,
		  RGS_DSCD,
		  PRJT_ID,
		  ERR_TYCD,
		  ERR_PRC_STEP_CD,
		  RGS_DT,
		  RGPE_NO,
		  ERR_TXT,
		  TTMN_SCD_DT,
		  TTMN_DT,
		  DVLPE_NO,
		  PL_NO,
		  TTMN_TXT,
		  BFJR_BZCD
  FROM pms_err_mng
  WHERE 1=1
  /*업무구분 1*/

	  AND BZCD = #{bzcd}

  /*등록단계구분*/

	  AND RGS_DSCD = #{rgs_dscd}

  /*결함유형*/

	  AND ERR_TYCD = #{err_tycd}

  /*처리단계1*/

	  AND PRC_STEP_CD = #{prc_step_cd}

  /*결함등록자*/

	  AND RGPE_NO = #{rgpe_no}

  /*담당자*/

	  AND DVLPE_NO = #{dvlpe_no}

  /*담당PL*/

	  AND PL_NO = #{pl_no}

 /*프로그램ID*/

	  AND PGM_ID = #{mng_id}

 /*결함내용*/

	  AND ERR_TXT = #{err_txt}
