mapper.select:
SELECT * FROM (SELECT A.BZCD ,A.RGS_DSCD ,A.CCTN_ID ,CASE WHEN B.PGM_NM IS NOT NULL THEN B.PGM_NM ELSE C.TST_CASE_NM END AS CCTN_NM ,A.ERR_TYCD ,A.ERR_PRC_STEP_CD ,NVL2(A.RGS_DT,STR_TO_DATE(A.RGS_DT , '%Y%m%d'),'') AS RGS_DT ,D.EMPNM AS RGPE_NM ,D.EMPNO AS RGPE_NO ,A.ERR_TXT ,A.TTMN_TXT ,NVL2(A.TTMN_SCD_DT,STR_TO_DATE(A.TTMN_SCD_DT , '%Y%m%d'),'') AS TTMN_SCD_DT ,NVL2(A.TTMN_DT,STR_TO_DATE(A.TTMN_DT , '%Y%m%d'),'') AS TTMN_DT ,E.EMPNM AS DVLPE_NM ,E.EMPNO AS DVLPE_NO ,F.EMPNM AS PL_NM ,F.EMPNO AS PL_NO ,A.BFJR_BZCD ,A.MNG_ID FROM pms_err_mng A LEFT OUTER JOIN pms_pgm_mng B ON B.PGM_ID = A.CCTN_ID AND B.BZCD = A.CCTN_BZCD AND B.PRJT_ID = A.PRJT_ID AND B.BKUP_ID = A.BKUP_ID LEFT OUTER JOIN pms_itg_tst_mng C ON C.TST_CASE_ID = A.CCTN_ID AND C.SQN_CD = A.CCTN_SQN_CD AND C.BZCD = A.CCTN_BZCD AND C.PRJT_ID = A.PRJT_ID AND C.BKUP_ID = A.BKUP_ID LEFT OUTER JOIN pms_emp_info D ON D.EMPNO = A.RGPE_NO AND D.PRJT_ID = A.PRJT_ID AND D.BKUP_ID = A.BKUP_ID LEFT OUTER JOIN pms_emp_info E ON E.EMPNO = A.DVLPE_NO AND E.PRJT_ID = A.PRJT_ID AND E.BKUP_ID = A.BKUP_ID LEFT OUTER JOIN pms_emp_info F ON F.EMPNO = A.PL_NO AND F.PRJT_ID = A.PRJT_ID AND F.BKUP_ID = A.BKUP_ID WHERE 1 = 1 AND A.BKUP_ID = #{bkup_id} AND A.PRJT_ID = #{prjt_id}
AND A.BZCD = #{bzcd}
AND A.RGS_DSCD = #{rgs_dscd}
AND A.ERR_TYCD = #{err_tycd}
AND A.ERR_PRC_STEP_CD = #{err_prc_step_cd}
AND A.ERR_PRC_STEP_CD IN ('100','200','300','400','500','600','700')
AND A.RGPE_NO = #{rgpe_no}
AND A.DVLPE_NO = #{dvlpe_no}
AND A.PL_NO = #{pl_no}
AND A.CCTN_ID LIKE CONCAT(#{cctn_id},'%')
AND A.ERR_TXT LIKE CONCAT(CONCAT('%',#{err_txt}),'%')
AND A.RGS_DT BETWEEN #{rgs_sta_dt} AND #{rgs_end_dt}
AND A.TTMN_DT BETWEEN #{ttmn_sta_dt} AND #{ttmn_end_dt}
) A WHERE 1 = 1
AND A.CCTN_NM LIKE CONCAT(CONCAT('%',#{cctn_nm}),'%')
ORDER BY A.BZCD, A.RGS_DT DESC
