mapper.update:
UPDATE pms_err_mng
SET BZCD            = #{bzcd}
  , RGS_DSCD        = #{rgs_dscd}
  , ERR_TYCD        = #{err_tycd}
  , ERR_TXT         = #{err_txt}
  , DVLPE_NO        = #{dvlpe_no}
  , PL_NO           = #{pl_no}
  , TTMN_SCD_DT     = REPLACE(#{ttmn_scd_dt}, '-', '')
  , TTMN_DT         = CASE
                          WHEN #{err_prc_step_cd} = '600' THEN DATE_FORMAT(NOW(), '%Y%m%d')
                          WHEN #{err_prc_step_cd} = '300' THEN ''
                          ELSE REPLACE(#{ttmn_dt}, '-', '') END
  , TTMN_TXT        = #{ttmn_txt}
  , ERR_PRC_STEP_CD = #{err_prc_step_cd}
  , BFJR_BZCD       = CASE WHEN #{bfjr_bzcd} != #{bzcd} THEN #{bzcd} ELSE BFJR_BZCD END
  , RGS_ATFL_MNG_ID = #{rgs_atfl_mng_id}
  , TTMN_ATFL_MNG_ID = #{ttmn_atfl_mng_id}
  , OPR_NO          = #{login_emp_no}
  , DB_CHG_TS       = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
WHERE 1 = 1
  AND BKUP_ID = #{bkup_id}
  AND PRJT_ID = #{prjt_id}
  AND MNG_ID = #{mng_id}

mapper.insert:

    SELECT CONCAT(CONCAT('E',#{err_tycd}),LPAD(NVL(MAX(CAST(SUBSTRING(MNG_ID,5,6) AS INT))+1,0),6,'0')) AS MNG_ID
    FROM pms_err_mng
    WHERE ERR_TYCD = #{err_tycd}
    AND PRJT_ID = #{prjt_id}
    AND BKUP_ID = #{bkup_id}

INSERT INTO pms_err_mng
(
BKUP_ID
,PRJT_ID
,MNG_ID
,BZCD
,RGS_DSCD
,ERR_TYCD
,ERR_TXT
,RGS_DT
,RGPE_NO
,DVLPE_NO
,PL_NO
,TTMN_SCD_DT
,TTMN_DT
,TTMN_TXT
,ERR_PRC_STEP_CD
,BFJR_BZCD
,RMRK
,CCTN_BZCD
,CCTN_ID
,CCTN_SQN_CD
,RGS_ATFL_MNG_ID
,TTMN_ATFL_MNG_ID
,OPR_NO
,DB_CHG_TS
)
VALUES
(
'0000000000'
, #{prjt_id}
, #{mng_id}
, #{bzcd}
, #{rgs_dscd}
, #{err_tycd}
, #{err_txt}
, DATE_FORMAT(NOW(),'%Y%m%d')
, #{rgpe_no}
, #{dvlpe_no}
, #{pl_no}
, ''
, REPLACE(#{ttmn_dt}, '-', '')
, #{ttmn_txt}
, #{err_prc_step_cd}
, ''
, ''
, #{cctn_bzcd}
, #{cctn_id}
, #{cctn_sqn_cd}
, ''
, ''
, #{login_emp_no}
, DATE_FORMAT(NOW(),'%Y%m%d%H%i%s')
)
