mapper.select:
SELECT A.MNG_CD , A.BZCD , A.STEP_CD , A.MNG_ID , A.HGRN_MNG_ID , A.ACVT_NM , A.TASK_NM , A.CRPE_NM , A.WBS_PRC_STS_CD , DATE_FORMAT(A.PLN_STA_DT,'%Y%-%m-%d') AS PLN_STA_DT , A.PLN_STA_TIM , DATE_FORMAT(A.PLN_END_DT,'%Y%-%m-%d') AS PLN_END_DT , A.PLN_END_TIM , DATE_FORMAT(A.ACL_STA_DT,'%Y%-%m-%d') AS ACL_STA_DT , A.ACL_STA_TIM , DATE_FORMAT(A.ACL_END_DT,'%Y%-%m-%d') AS ACL_END_DT , A.ACL_END_TIM , A.WGT_RT , CAST(A.PRG_RT AS signed integer) AS PRG_RT , A.RMRK , A.SORT , A.ATFL_MNG_ID , A.PRJT_ID , (SELECT COUNT(*) FROM pms_wbs_mng B WHERE B.HGRN_MNG_ID = A.MNG_ID AND B.MNG_CD = A.MNG_CD AND B.BZCD = A.BZCD AND B.PRJT_ID = A.PRJT_ID AND B.BKUP_ID = A.BKUP_ID) AS WBS_CNT FROM pms_wbs_mng A WHERE 1 = 1 AND A.BKUP_ID = #{bkup_id} AND A.PRJT_ID = #{prjt_id}
AND A.BZCD = #{bzcd}
AND A.MNG_CD = #{mng_cd}
AND A.WBS_PRC_STS_CD = #{wbs_prc_sts_cd}
AND A.CRPE_NM LIKE CONCAT(CONCAT('%',#{crpe_nm}),'%')
AND A.PLN_END_DT BETWEEN #{pln_sta_dt} AND #{pln_end_dt}
AND A.ACL_END_DT BETWEEN #{acl_sta_dt} AND #{acl_end_dt}
ORDER BY A.SORT

mapper.insert:
INSERT INTO pms_wbs_mng
	(
          BKUP_ID
        , PRJT_ID
        , MNG_CD
        , MNG_ID
        , BZCD
        , HGRN_MNG_ID
        , STEP_CD
        , ACVT_NM
        , TASK_NM
        , CRPE_NM
        , PLN_STA_DT
        , PLN_STA_TIM
        , PLN_END_DT
        , PLN_END_TIM
        , ACL_STA_DT
        , ACL_STA_TIM
        , ACL_END_DT
        , ACL_END_TIM
        , WGT_RT
        , PRG_RT
        , WBS_PRC_STS_CD
        , RMRK
        , SORT
        , ATFL_MNG_ID
        , OPR_NO
        , DB_CHG_TS
	) VALUES
	    (
		   '0000000000'
	      , #{prjt_id}
          , #{mng_cd}
          , #{mng_id}
          , #{bzcd}
          , #{hgrn_mng_id}
          , #{step_cd}
          , #{acvt_nm}
          , #{task_nm}
          , #{crpe_nm}
          , REPLACE(#{pln_sta_dt}, '-', '')
          , #{pln_sta_tim}
		  , REPLACE(#{pln_end_dt}, '-', '')
          , #{pln_end_tim}
          , ''
          , ''
          , ''
          , ''
          , #{wgt_rt}
          , #{prg_rt}
          , '100'
          , #{rmrk}
          , #{sort}
          , #{atfl_mng_id}
		  , #{login_emp_no}
		  , DATE_FORMAT(NOW(),'%Y%m%d%H%i%s')
		)

mapper.delete:
	 DELETE FROM pms_wbs_mng
	  WHERE 1 = 1
		AND BKUP_ID  = #{bkup_id}
        AND PRJT_ID  = #{prjt_id}
        AND MNG_CD   = #{mng_cd}
        AND BZCD     = #{bzcd}
