mapper.select:
SELECT A.MNG_CD , A.BZCD , A.STEP_CD , A.MNG_ID , A.HGRN_MNG_ID , CONCAT(B.ETC_TXT1, A.ACVT_NM) AS ACVT_NM , A.TASK_NM , A.CRPE_NM , A.WBS_PRC_STS_CD , DATE_FORMAT(A.PLN_STA_DT,'%Y%-%m-%d') AS PLN_STA_DT , A.PLN_STA_TIM , DATE_FORMAT(A.PLN_END_DT,'%Y%-%m-%d') AS PLN_END_DT , A.PLN_END_TIM , DATE_FORMAT(A.ACL_STA_DT,'%Y%-%m-%d') AS ACL_STA_DT , A.ACL_STA_TIM , DATE_FORMAT(A.ACL_END_DT,'%Y%-%m-%d') AS ACL_END_DT , A.ACL_END_TIM , A.WGT_RT , A.PRG_RT , A.RMRK , A.SORT , A.ATFL_MNG_ID , A.PRJT_ID , (SELECT COUNT(*) FROM pms_wbs_mng B WHERE B.HGRN_MNG_ID = A.MNG_ID AND B.MNG_CD = A.MNG_CD AND B.PRJT_ID = A.PRJT_ID AND B.BKUP_ID = A.BKUP_ID) AS WBS_CNT FROM pms_wbs_mng A LEFT OUTER JOIN pms_cd_del_mng B ON B.DTLS_TYCD = A.STEP_CD AND B.GRP_TYCD = '1000000020' AND B.PRJT_ID = A.PRJT_ID AND B.BKUP_ID = A.BKUP_ID WHERE 1 = 1 AND A.BKUP_ID = #{bkup_id} AND A.PRJT_ID = #{prjt_id}
AND A.BZCD = #{bzcd}
AND A.MNG_CD = #{mng_cd}
AND A.WBS_PRC_STS_CD = #{wbs_prc_sts_cd}
AND A.ACVT_NM LIKE CONCAT(CONCAT('%',#{acvt_nm}),'%')
AND A.CRPE_NM LIKE CONCAT(CONCAT('%',#{crpe_nm}),'%')
AND A.PLN_END_DT BETWEEN #{pln_sta_dt} AND #{pln_end_dt}
AND A.ACL_END_DT BETWEEN #{acl_sta_dt} AND #{acl_end_dt}
ORDER BY A.MNG_ID
ORDER BY A.BZCD, A.MNG_ID

mapper.insert:
INSERT INTO pms_wbs_mng
( BKUP_ID
, PRJT_ID
, MNG_CD
, MNG_ID
, BZCD
, HGRN_MNG_ID
, STEP_CD
, ACVT_NM
, TASK_NM
, CRPE_NM
, PLN_STA_DT
, PLN_STA_TIM
, PLN_END_DT
, PLN_END_TIM
, ACL_STA_DT
, ACL_STA_TIM
, ACL_END_DT
, ACL_END_TIM
, WGT_RT
, PRG_RT
, WBS_PRC_STS_CD
, RMRK
, SORT
, ATFL_MNG_ID
, OPR_NO
, DB_CHG_TS)
VALUES ( '0000000000'
       , #{prjt_id}
       , #{mng_cd}
       , #{mng_id}
       , #{bzcd}
       , #{hgrn_mng_id}
       , #{step_cd}
       , LTRIM(REPLACE(REPLACE(#{acvt_nm}, '➥', ''),'☞',''))
       , #{task_nm}
       , #{crpe_nm}
       , REPLACE(#{pln_sta_dt}, '-', '')
       , #{pln_sta_tim}
       , REPLACE(#{pln_end_dt}, '-', '')
       , #{pln_end_tim}
       , ''
       , ''
       , ''
       , ''
       , #{wgt_rt}
       , #{prg_rt}
       , '100'
       , #{rmrk}
       , #{sort}
       , #{atfl_mng_id}
       , #{login_emp_no}
       , DATE_FORMAT(NOW(), '%Y%m%d%H%i%s'))

mapper.delete:
DELETE
FROM pms_wbs_mng
WHERE 1 = 1
  AND BKUP_ID = #{bkup_id}
  AND PRJT_ID = #{prjt_id}
  AND MNG_CD  = #{mng_cd}

  AND BZCD    = #{bzcd}


  AND MNG_ID  = #{mng_id}

mapper.update:

UPDATE pms_wbs_mng
SET    HGRN_MNG_ID     = #{hgrn_mng_id}
  ,STEP_CD         = #{step_cd}
  ,ACVT_NM         = LTRIM(REPLACE(REPLACE(#{acvt_nm}, '➥', ''),'☞',''))
  ,TASK_NM         = #{task_nm}
  ,CRPE_NM         = #{crpe_nm}
  ,PLN_STA_DT      = CASE WHEN #{login_aut_cd} IN ('500','600') THEN #{pln_sta_dt}  ELSE PLN_STA_DT  END
  ,PLN_STA_TIM     = CASE WHEN #{login_aut_cd} IN ('500','600') THEN #{pln_sta_tim} ELSE PLN_STA_TIM END
  ,PLN_END_DT      = CASE WHEN #{login_aut_cd} IN ('500','600') THEN #{pln_end_dt}  ELSE PLN_END_DT  END
  ,PlN_END_TIM     = CASE WHEN #{login_aut_cd} IN ('500','600') THEN #{pln_end_tim} ELSE PLN_END_TIM END
  ,ACL_STA_DT      = CASE WHEN #{wbs_prc_sts_cd} = '200' OR (#{prg_rt} > 0 AND #{prg_rt} < 100) THEN DATE_FORMAT(NOW(),'%Y%m%d') ELSE ACL_STA_DT  END
  ,ACL_STA_TIM     = CASE WHEN #{wbs_prc_sts_cd} = '200' OR (#{prg_rt} > 0 AND #{prg_rt} < 100) THEN DATE_FORMAT(NOW(),'%H:%i')   ELSE ACL_STA_TIM END
  ,ACL_END_DT      = CASE WHEN #{wbs_prc_sts_cd} = '300' OR #{prg_rt} >= 100 THEN DATE_FORMAT(NOW(),'%Y%m%d') ELSE ACL_END_DT  END
  ,ACL_END_TIM     = CASE WHEN #{wbs_prc_sts_cd} = '300' OR #{prg_rt} >= 100 THEN DATE_FORMAT(NOW(),'%H:%i')  ELSE ACL_END_TIM END
  ,WGT_RT          = CASE WHEN #{login_aut_cd} IN ('500','600') THEN #{wgt_rt}  ELSE WGT_RT  END
  ,PRG_RT          = #{prg_rt}
  ,WBS_PRC_STS_CD  = CASE WHEN #{mng_cd} = '100' THEN
                                  CASE WHEN #{prg_rt} = 0 THEN '100'
                                       WHEN #{prg_rt} != 0 AND #{prg_rt} < 100 THEN '200' ELSE '300' END
                         ELSE #{wbs_prc_sts_cd} END
  ,RMRK             = #{rmrk}
  ,SORT             = #{sort}
  ,OPR_NO           = #{login_emp_no}
  ,DB_CHG_TS        = DATE_FORMAT(NOW(),'%Y%m%d%H%i%s')
WHERE 1 = 1
AND BKUP_ID  = #{bkup_id}
AND PRJT_ID  = #{prjt_id}
AND MNG_CD   = #{mng_cd}
AND MNG_ID   = #{mng_id}
AND BZCD     = #{bzcd}
