mapper.update:
UPDATE pms_rq_mng
SET    BZCD	           = #{bzcd}
  ,REQ_DSCD	       = #{req_dscd}
  ,REQPE_NO	       = #{reqpe_no}
  ,REQ_DT	         = #{req_dt}
  ,REQ_TXT	       = #{req_txt}
  ,PRCPE_NO	       = #{prcpe_no}
  ,PRC_DT	         = CASE WHEN #{req_prc_step_cd} = '400' THEN DATE_FORMAT(NOW(),'%Y%m%d') ELSE '' END
  ,PRC_TXT	       = #{prc_txt}
  ,REQ_PRC_STEP_CD = #{req_prc_step_cd}
  ,OPR_NO	         = #{login_emp_no}
  ,DB_CHG_TS	     = DATE_FORMAT(NOW(),'%Y%m%d%H%i%s')
WHERE  1 = 1
  AND    BKUP_ID  = #{bkup_id}
  AND    PRJT_ID  = #{prjt_id}
  AND    MNG_ID   = #{mng_id}
