mapper.select:
SELECT CASE WHEN COUNT(*) = 0 THEN 'N' ELSE 'Y' END AS SAVE_YN
FROM   pms_as_to_map
WHERE  BKUP_ID       = #{bkup_id}
AND    PRJT_ID       = #{prjt_id}
AND    AS_PGM_ID     = #{as_pgm_id}
AND    AS_PGM_DIS_CD = #{as_pgm_dis_cd}

mapper.update:
UPDATE pms_as_to_map
SET    AS_PGM_NM     = #{as_pgm_nm}
  ,TO_PGM_ID     = #{to_pgm_id}
  ,TO_PGM_NM     = #{to_pgm_nm}
  ,USE_PGM_TXT   = #{use_pgm_txt}
  ,DVLPE_NO      = #{dvlpe_no}
  ,TRN_STT_CD    = #{trn_stt_cd}
  ,FRCS_STA_DT   = CASE WHEN #{login_aut_cd} IN ('500','600','900') THEN REPLACE(#{frcs_sta_dt}, '-', '') ELSE FRCS_STA_DT  END
  ,FRCS_END_DT   = CASE WHEN #{login_aut_cd} IN ('500','600','900') THEN REPLACE(#{frcs_end_dt}, '-', '') ELSE FRCS_END_DT  END
  ,STA_DT        = CASE WHEN #{trn_stt_cd} = '200' AND STA_DT = '' THEN DATE_FORMAT(NOW(),'%Y%m%d') ELSE STA_DT END
  ,END_DT        = CASE WHEN #{trn_stt_cd} = '300'                 THEN DATE_FORMAT(NOW(),'%Y%m%d') ELSE END_DT END
  ,RMRK          = #{rmrk}
  ,OPR_NO        = #{login_emp_no}
  ,DB_CHG_TS     = DATE_FORMAT(NOW(),'%Y%m%d%H%i%s')
WHERE  1 = 1
  AND    BKUP_ID       = '0000000000'
  AND    PRJT_ID       = #{prjt_id}
  AND    AS_PGM_ID     = #{as_pgm_id}
  AND    AS_PGM_DIS_CD = #{as_pgm_dis_cd}

mapper.insert:
INSERT INTO pms_as_to_map
(
    BKUP_ID
,PRJT_ID
,AS_PGM_ID
,AS_PGM_DIS_CD
,AS_PGM_NM
,TO_PGM_ID
,TO_PGM_NM
,USE_PGM_TXT
,DVLPE_NO
,TRN_STT_CD
,FRCS_STA_DT
,FRCS_END_DT
,STA_DT
,END_DT
,RMRK
,OPR_NO
,DB_CHG_TS
)
VALUES
    (
        '0000000000'
    , #{prjt_id}
    , #{as_pgm_id}
    , #{as_pgm_dis_cd}
    , #{as_pgm_nm}
    , #{to_pgm_id}
    , #{to_pgm_nm}
    , #{use_pgm_txt}
    , #{dvlpe_no}
    , '100'
    , REPLACE(#{frcs_sta_dt}, '-', '')
    , REPLACE(#{frcs_end_dt}, '-', '')
    , ''
    , ''
    , #{rmrk}
    , #{login_emp_no}
    , DATE_FORMAT(NOW(),'%Y%m%d%H%i%s')
    )
