mapper.select:
SELECT A.RQS_ID ,A.RSN_RQS ,DATE_FORMAT(A.RQS_DT,'%Y-%m-%d') AS RQS_DT ,A.RLS_DT AS RLS_DT ,A.DSTR ,A.PRCS_STTS_CD ,A.RQS_NO ,B.EMPNM AS RQS_NM ,A.RVW_NO ,C.EMPNM AS RVW_NM ,A.APRV_NO ,D.EMPNM AS APRV_NM ,A.RMRMK ,A.ATFL_MNG_ID ,A.BKUP_ID ,A.PRJT_ID ,E.ORG_FILE_NM ,A.TAR_CHG_TS ,A.INS_DPLY_TS ,A.DR_DPLY_TS ,A.OPR_DPLY_TS ,CASE WHEN A.INS_DPLY_TS = '' AND A.DR_DPLY_TS = '' AND A.OPR_DPLY_TS = '' THEN 'Y' ELSE 'N' END AS SAVE_CHG_YN FROM rls_rqs_mng A LEFT OUTER JOIN pms_emp_info B ON B.EMPNO = A.RQS_NO AND B.PRJT_ID = A.PRJT_ID AND B.BKUP_ID = A.BKUP_ID LEFT OUTER JOIN pms_emp_info C ON C.EMPNO = A.RVW_NO AND C.PRJT_ID = A.PRJT_ID AND C.BKUP_ID = A.BKUP_ID LEFT OUTER JOIN pms_emp_info D ON D.EMPNO = A.APRV_NO AND D.PRJT_ID = A.PRJT_ID AND D.BKUP_ID = A.BKUP_ID LEFT OUTER JOIN pms_atfl_mng E ON E.SQNO = 1 AND E.FILE_RGS_DSCD = '806' AND E.ATFL_MNG_ID = A.ATFL_MNG_ID AND E.PRJT_ID = A.PRJT_ID AND E.BKUP_ID = A.BKUP_ID WHERE A.BKUP_ID = #{bkup_id} AND A.PRJT_ID = #{prjt_id}
AND A.RQS_DT = REPLACE(#{rqs_dt}, '-', '')
AND A.RLS_DT LIKE CONCAT(#{rls_dt},'%')
AND A.RQS_NO = #{rqs_no}
AND A.RVW_NO = #{rvw_no}
AND A.APRV_NO = #{aprv_no}
/*배포상태*/
AND A.PRCS_STTS_CD = #{prcs_stts_cd}
/*배포구분*/
AND A.DSTR = #{dstr}
AND A.PRCS_STTS_CD NOT IN ('900','910')
ORDER BY A.DSTR, A.PRCS_STTS_CD DESC, A.TAR_CHG_TS

mapper.update:
UPDATE rls_rqs_mng
SET    RSN_RQS      = #{rsn_rqs}
      ,RLS_DT       = CASE WHEN #{prcs_stts_cd} = '500' AND PRCS_STTS_CD != '500' THEN DATE_FORMAT(NOW(),'%Y-%m-%d %H:%i:%s') ELSE #{rls_dt} END
      ,DSTR         = #{dstr}
      ,PRCS_STTS_CD = #{prcs_stts_cd}
      ,RQS_NO       = #{rqs_no}
      ,RVW_NO       = #{rvw_no}
      ,APRV_NO      = #{aprv_no}
      ,RMRMK        = #{rmrmk}
      ,TAR_CHG_TS   = CASE WHEN #{prcs_stts_cd} IN ('100','180') THEN '' ELSE TAR_CHG_TS END
      ,INS_DPLY_TS  = CASE WHEN #{prcs_stts_cd} IN ('100','180','190') THEN ''
                           WHEN #{prcs_stts_cd} = '500' THEN DATE_FORMAT(NOW(),'%Y-%m-%d %H:%i:%s') ELSE INS_DPLY_TS END
      ,DR_DPLY_TS   = CASE WHEN #{prcs_stts_cd} IN ('100','180','190') THEN ''
                           WHEN #{prcs_stts_cd} = '510' THEN DATE_FORMAT(NOW(),'%Y-%m-%d %H:%i:%s') ELSE DR_DPLY_TS END
      ,OPR_DPLY_TS  = CASE WHEN #{prcs_stts_cd} IN ('100','180','190') THEN ''
                           WHEN #{prcs_stts_cd} = '520' THEN DATE_FORMAT(NOW(),'%Y-%m-%d %H:%i:%s') ELSE OPR_DPLY_TS END
      ,OPR_NO       = #{login_emp_no}
      ,DB_CHG_TS    = DATE_FORMAT(NOW(),'%Y%m%d%H%i%s')
WHERE  RQS_ID       = #{rqs_id}
AND    BKUP_ID      = '0000000000'
AND    PRJT_ID      = #{prjt_id}

UPDATE rls_rqs_mng
SET    PRCS_STTS_CD = #{prcs_stts_cd}
      ,TAR_CHG_TS   = CASE WHEN #{prcs_stts_cd} = '190' THEN DATE_FORMAT(NOW(),'%Y-%m-%d %H:%i:%s') ELSE '' END
      ,OPR_NO       = #{login_emp_no}
      ,DB_CHG_TS    = DATE_FORMAT(NOW(),'%Y%m%d%H%i%s')
WHERE  RQS_ID       = #{rqs_id}
  AND  BKUP_ID      = '0000000000'
  AND  PRJT_ID      = #{prjt_id}

UPDATE rls_pck_lst
SET    ERR_MSG_CNT = #{err_msg_cnt}
      ,SCS_YN      = #{scs_yn}
      ,OPR_NO      = #{login_emp_no}
      ,DB_CHG_TS   = DATE_FORMAT(NOW(),'%Y%m%d%H%i%s')
WHERE  SQNO        = #{sqno}
  AND  RQS_ID      = #{rqs_id}
  AND  BKUP_ID     = '0000000000'
  AND  PRJT_ID     = #{prjt_id}

mapper.insert:
INSERT INTO rls_rqs_mng
(BKUP_ID
,PRJT_ID
,RQS_ID
,RSN_RQS
,RQS_DT
,RLS_DT
,DSTR
,PRCS_STTS_CD
,RQS_NO
,RVW_NO
,APRV_NO
,RMRMK
,ATFL_MNG_ID
,TAR_CHG_TS
,INS_DPLY_TS
,DR_DPLY_TS
,OPR_DPLY_TS
,OPR_NO
,DB_CHG_TS
)
VALUES
    ('0000000000'
    , #{prjt_id}
    , #{rqs_id}
    , #{rsn_rqs}
    , DATE_FORMAT(NOW(),'%Y%m%d')
    , #{rls_dt}
    , #{dstr}
    , #{prcs_stts_cd}
    , #{rqs_no}
    , #{rvw_no}
    , #{aprv_no}
    , #{rmrmk}
    , ''
    , ''
    , ''
    , ''
    , ''
    , #{login_emp_no}
    , DATE_FORMAT(NOW(),'%Y%m%d%H%i%s')
    )

mapper.delete:

DELETE
FROM   rls_pck_lst
WHERE  RQS_ID  = #{rqs_id}
AND    PRJT_ID = #{prjt_id}
AND    BKUP_ID = '0000000000'
