mapper.select:
SELECT REAL_PRJT_ID
FROM   pms_week_rpt_mng
WHERE  WEEK_SQN_CD  =  #{week_sqn_cd}
AND    WEEK_YYMM    = REPLACE(#{week_yymm},'-','')
AND    REAL_PRJT_ID = #{real_prjt_id}
AND    PRJT_ID      = #{prjt_id}
AND    BKUP_ID      = '0000000000'

mapper.insert:
INSERT INTO pms_week_rpt_mng
(
 BKUP_ID
,PRJT_ID
,REAL_PRJT_ID
,WEEK_YYMM
,WEEK_SQN_CD
,DEPT_CD
,PM_NO
,ALL_REAL_PRG
,ALL_PRED_PRG
,STEP_NM
,STEP_REAL_PRG
,STEP_PRED_PRG
,PRG_TXT
,ISS_TXT
,REQ_TXT
,ATFL_MNG_ID
,OPR_NO
,DB_CHG_TS
)
VALUES
(
'0000000000'
, #{prjt_id}
, #{real_prjt_id}
, REPLACE(#{week_yymm},'-','')
, #{week_sqn_cd}
, #{dept_cd}
, #{pm_no}
, #{all_real_prg}
, #{all_pred_prg}
, #{step_nm}
, #{step_real_prg}
, #{step_pred_prg}
, #{prg_txt}
, #{iss_txt}
, #{req_txt}
, #{atfl_mng_id}
, #{login_emp_no}
, DATE_FORMAT(NOW(),'%Y%m%d%H%i%s')
)

mapper.update:
UPDATE pms_week_rpt_mng
SET    PM_NO	 = CASE WHEN #{bef_aut_cd} IN ('500','600','900') THEN #{pm_no} ELSE PM_NO END
  ,ALL_REAL_PRG	 = #{all_real_prg}
  ,ALL_PRED_PRG	 = #{all_pred_prg}
  ,STEP_NM	     = #{step_nm}
  ,STEP_REAL_PRG = #{step_real_prg}
  ,STEP_PRED_PRG = #{step_pred_prg}
  ,PRG_TXT	     = #{prg_txt}
  ,ISS_TXT	     = CASE WHEN PM_NO = #{bef_emp_no} OR #{bef_aut_cd} IN ('500','600','900') THEN #{iss_txt} ELSE ISS_TXT END
  ,REQ_TXT	     = #{req_txt}
  ,ATFL_MNG_ID   = #{atfl_mng_id}
  ,OPR_NO        = #{login_emp_no}
  ,DB_CHG_TS     = DATE_FORMAT(NOW(),'%Y%m%d%H%i%s')
WHERE  1 = 1
  AND    WEEK_SQN_CD     = #{week_sqn_cd}
  AND    WEEK_YYMM       = REPLACE(#{week_yymm},'-','')
  AND    REAL_PRJT_ID	 = #{real_prjt_id}
  AND    DEPT_CD         = #{dept_cd}
  AND    PRJT_ID         = #{prjt_id}
  AND    BKUP_ID	     = '0000000000'
