mapper.select:
SELECT A.PRJT_ID ,A.BKUP_ID ,A.BZCD ,A.EMPNO ,A.EMPNM ,A.RANK_NM ,A.CATN_DCD ,A.LGN_PWD ,A.AUT_CD ,A.REAL_PRJT_ID ,A.DEPT_CD ,A.DEL_YN ,B.SORT_SEQ ,DATE_FORMAT(A.INP_DT,'%Y-%m-%d') AS INP_DT ,DATE_FORMAT(A.WTH_DT,'%Y-%m-%d') AS WTH_DT FROM pms_emp_info A LEFT OUTER JOIN pms_cd_del_mng B ON B.DTLS_TYCD = A.BZCD AND B.GRP_TYCD = '1000000001' AND B.PRJT_ID = A.PRJT_ID AND B.BKUP_ID = A.BKUP_ID WHERE 1 = 1 AND A.BKUP_ID = #{bkup_id} AND A.PRJT_ID = #{prjt_id}
ORDER BY A.CATN_DCD, SUBSTR(A.DEPT_CD,1,6), A.DEPT_CD, B.SORT_SEQ DESC, A.EMPNM
ORDER BY A.EMPNM

mapper.delete:
DELETE
FROM   pms_emp_info
WHERE  1 = 1
  AND    EMPNO	     = #{empno}
  AND    PRJT_ID       = #{prjt_id}
  AND    BKUP_ID	     = #{bkup_id}

mapper.update:
UPDATE pms_emp_info
SET    EMPNM         = #{empnm}
  ,RANK_NM       = #{rank_nm}
  ,LGN_PWD       = #{lgn_pwd}
  ,BZCD          = #{bzcd}
  ,CATN_DCD      = #{catn_dcd}
  ,AUT_CD        = #{aut_cd}
  ,REAL_PRJT_ID  = #{real_prjt_id}
  ,DEPT_CD       = #{dept_cd}
  ,DEL_YN        = #{del_yn}
  ,INP_DT        = REPLACE(#{inp_dt}, '-', '')
  ,WTH_DT        = REPLACE(#{wth_dt}, '-', '')
  ,OPR_NO        = #{login_emp_no}
  ,DB_CHG_TS     = DATE_FORMAT(NOW(),'%Y%m%d%H%i%s')
WHERE  1 = 1
  AND    EMPNO	       = #{empno}
  AND    PRJT_ID       = #{prjt_id}
  AND    BKUP_ID	     = #{bkup_id}

mapper.insert:
