     , #{dept_cd}

      , 'Y'


      , 'N'

     , REPLACE(#{inp_dt}, '-', '')
     , REPLACE(#{wth_dt}, '-', '')
     , #{login_emp_no}       /*조작자번호*/
     , DATE_FORMAT(NOW(),'%Y%m%d%H%i%s')   /*DB변경일시*/
     )

