mapper.update:
UPDATE pms_holiday_mng
SET    PRJT_ID   = #{prjt_id}
  ,DATE          = REPLACE(#{date}, '-', '')
  ,DATE_CD       = #{date_cd}
  ,HOLIDAY_CD    = #{holiday_cd}
WHERE  1 = 1
  AND    DATE	         = REPLACE(#{date}, '-', '')
  AND    PRJT_ID       = #{prjt_id}

