mapper.select:
SELECT A.PRJT_ID /*프로젝트 */ , B.DTLS_TYNM AS DEPT_NM /*부서명 */ , A.BZCD /*업무구분 */ , A.EMPNO /*직원번호 */ , A.EMPNM /*직원명 */ , A.RANK_NM /*직급명 */ , A.CATN_DCD /*구성원구분 */ , A.LGN_PWD /*로그인비밀번호 */ , A.AUT_CD /*권한ID */ FROM pms_emp_info A LEFT OUTER JOIN pms_cd_del_mng B ON B.DTLS_TYCD = A.DEPT_CD AND B.GRP_TYCD = '1000000044' AND B.PRJT_ID = A.PRJT_ID AND B.BKUP_ID = A.BKUP_ID
WHERE 1=1 /*프로젝트구분*/
AND A.PRJT_ID = #{prjt_id}
/*백업ID*/
AND A.BKUP_ID = #{bkup_id}
/*업무구분*/
AND A.BZCD = #{bzcd}
/*직원번호*/
AND A.EMPNO LIKE CONCAT('%', #{empno}, '%')
/*직원명*/
AND A.EMPNM LIKE CONCAT('%', #{empnm}, '%')
