mapper.delete:
DELETE
FROM pms_man_pfle_info
WHERE 1 = 1
  AND BKUP_ID = '0000000000'
  AND PRJT_ID = #{prjt_id}
  AND MAN_NO = #{man_no}

DELETE
FROM pms_cpy_his_lst
WHERE 1 = 1
  AND BKUP_ID = '0000000000'
  AND PRJT_ID = #{prjt_id}
  AND MAN_NO = #{man_no}

DELETE
FROM pms_career_lst
WHERE 1 = 1
  AND BKUP_ID = '0000000000'
  AND PRJT_ID = #{prjt_id}
  AND MAN_NO = #{man_no}
