mapper.update:
		UPDATE pms_prj_prs_rqs
		SET    SCH_ENT_DT  = REGEXP_REPLACE(#{sch_ent_dt}, '[^[:alnum:][:space:]]+', '')  /* 정규식 사용하여 '-', '.' 등 모든 특수문자 제거 */
		  ,SKILL_GRD   = #{skill_grd}
		  ,MAIN_SKILL  = #{main_skill}
		  ,DUTY_TXT    = #{duty_txt}
		  ,OTH_CNT     = #{oth_cnt}
		  ,NMBR_RCRT   = #{nmbr_rcrt}
		  ,OPR_NO      = #{login_emp_no}
		  ,DB_CHG_TS   = DATE_FORMAT(NOW(),'%Y%m%d%H%i%s')
		WHERE  1 = 1
		  AND    BKUP_ID      = '0000000000'
		  AND    PRJT_ID      = #{prjt_id}
 		  AND    REAL_PRJT_ID = #{real_prjt_id}
		  AND    SQNO         = #{sqno}

UPDATE pms_prj_prs_rqs
SET APLC_DTLS   = CASE WHEN LENGTH(TRIM(NVL(APLC_DTLS,''))) = 0 THEN #{login_emp_nm}
                       WHEN INSTR(APLC_DTLS, #{login_emp_nm}) = 0 THEN CONCAT(APLC_DTLS,' , ', #{login_emp_nm}) ELSE APLC_DTLS END
  ,OPR_NO      = #{login_emp_no}
  , DB_CHG_TS   = DATE_FORMAT(NOW(),'%Y%m%d%H%i%s')
WHERE 1 = 1
  AND BKUP_ID      = '0000000000'
  AND PRJT_ID      = #{prjt_id}
  AND REAL_PRJT_ID = #{real_prjt_id}
  AND SQNO         = #{sqno}

UPDATE pms_prj_prs_rqs
SET    DEL_YN      = 'Y'
  ,OPR_NO      = #{login_emp_no}
  ,DB_CHG_TS   = DATE_FORMAT(NOW(),'%Y%m%d%H%i%s')
WHERE  1 = 1
  AND    BKUP_ID      = '0000000000'
  AND    PRJT_ID      = #{prjt_id}
  AND    REAL_PRJT_ID = #{real_prjt_id}
  AND    SQNO         = #{sqno}

mapper.insert:

	SELECT NVL(MAX(SQNO)+1,1) AS SQNO
	FROM   pms_prj_prs_rqs
	WHERE  BKUP_ID      = '0000000000'
	AND    PRJT_ID      = #{prjt_id}
	AND    REAL_PRJT_ID = #{real_prjt_id}


INSERT INTO pms_prj_prs_rqs
(
BKUP_ID
,PRJT_ID
,REAL_PRJT_ID
,SQNO
,SCH_ENT_DT
,SKILL_GRD
,MAIN_SKILL
,DUTY_TXT
,OTH_CNT
,NMBR_RCRT
,APLC_DTLS
,REQPE_NO
,DEL_YN
,OPR_NO
,DB_CHG_TS
)
VALUES
(
'0000000000'
, #{prjt_id}
, #{real_prjt_id}
, #{sqno}
, REGEXP_REPLACE(#{sch_ent_dt}, '[^[:alnum:][:space:]]+', '')  /* 정규식 사용하여 '-', '.' 등 모든 특수문자 제거 */
, #{skill_grd}
, #{main_skill}
, #{duty_txt}
, #{oth_cnt}
, #{nmbr_rcrt}
, ''
, #{login_emp_no}
, 'N'
, #{login_emp_no}
, DATE_FORMAT(NOW(),'%Y%m%d%H%i%s')
)
