mapper.select:
SELECT C.DTLS_TYNM AS DEPT_NM ,D.DTLS_TYNM AS HDQ_NM ,E.DTLS_TYNM AS TM_NM ,B.RANK_NM ,B.EMPNO ,B.EMPNM , CASE WHEN A.ENT_DT IS NOT NULL AND A.ENT_DT != '' THEN STR_TO_DATE(A.ENT_DT , '%Y%m%d') ELSE '' END AS ENT_DT ,A.INP_PRJ_NM , CASE WHEN A.INP_DT IS NOT NULL AND A.INP_DT != '' THEN STR_TO_DATE(A.INP_DT , '%Y%m%d') ELSE '' END AS INP_DT , CASE WHEN A.WTH_DT IS NOT NULL AND A.WTH_DT != '' THEN STR_TO_DATE(A.WTH_DT , '%Y%m%d') ELSE '' END AS WTH_DT ,A.PRJ_TYP_NM ,A.PRF_AR ,A.RMRK ,A.INP_CLS_CD ,A.WTH_SCH_YN ,CONCAT(SUBSTR(B.DEPT_CD,1,4),'0000') AS DEPT_CD FROM pms_emp_info B LEFT OUTER JOIN pms_inp_mnp_stt A ON A.EMPNO = B.EMPNO AND A.PRJT_ID = B.PRJT_ID AND A.BKUP_ID = B.BKUP_ID LEFT OUTER JOIN pms_cd_del_mng C ON C.DTLS_TYCD = CONCAT(SUBSTR(B.DEPT_CD,1,4),'0000') AND C.GRP_TYCD = '1000000044' AND C.PRJT_ID = B.PRJT_ID AND C.BKUP_ID = B.BKUP_ID LEFT OUTER JOIN pms_cd_del_mng D ON D.DTLS_TYCD = CONCAT(SUBSTR(B.DEPT_CD,1,6),'00') AND D.GRP_TYCD = '1000000044' AND D.PRJT_ID = B.PRJT_ID AND D.BKUP_ID = B.BKUP_ID LEFT OUTER JOIN pms_cd_del_mng E ON E.DTLS_TYCD = B.DEPT_CD AND E.GRP_TYCD = '1000000044' AND E.PRJT_ID = B.PRJT_ID AND E.BKUP_ID = B.BKUP_ID WHERE B.BKUP_ID = #{bkup_id} AND B.PRJT_ID = #{prjt_id}
AND CONCAT(SUBSTR(B.DEPT_CD,1,4),'0000') = #{dept_cd}
ORDER BY C.DTLS_TYNM, D.DTLS_TYNM, E.DTLS_TYNM, A.INP_PRJ_NM, B.EMPNM

mapper.delete:
DELETE FROM pms_inp_mnp_stt
WHERE  1 = 1
AND    BKUP_ID      = '0000000000'
AND    PRJT_ID      = #{prjt_id}

	AND    DEPT_CD = #{dept_cd}

mapper.insert:
INSERT INTO pms_inp_mnp_stt
(
	BKUP_ID
,PRJT_ID
,EMPNO
,ENT_DT
,INP_PRJ_NM
,INP_DT
,WTH_DT
,PRF_AR
,PRJ_TYP_NM
,INP_CLS_CD
,RMRK
,WTH_SCH_YN
,DEPT_CD
,OPR_NO
,DB_CHG_TS
)
VALUES
	(
		'0000000000'
	, #{prjt_id}
	, #{empno}
	, REGEXP_REPLACE(#{ent_dt}, '[^[:alnum:][:space:]]+', '') /* 정규식 사용하여 '-', '.' 등 모든 특수문자 제거 */
	, #{inp_prj_nm}
	, REGEXP_REPLACE(#{inp_dt}, '[^[:alnum:][:space:]]+', '')
	, REGEXP_REPLACE(#{wth_dt}, '[^[:alnum:][:space:]]+', '')
	, #{prf_ar}
	, #{prj_typ_nm}
	, #{inp_cls_cd}
	, #{rmrk}
	, #{wth_sch_yn}
	, #{dept_cd}
	, #{login_emp_no}
	, DATE_FORMAT(NOW(),'%Y%m%d%H%i%s')
	)
