mapper.select:
SELECT BKUP_ID , PRJT_ID , PGM_ID , BZCD , SQN_CD , SCNR_ID , SCNR_NM , TST_CASE_ID , TST_CASE_NM , FRCS_STA_DT , FRCS_END_DT , DVLPE_CNF_DT , PL_CNF_DT , DVLPE_ENO , PL_ENO , CRPE_ENO , ITG_TST_PRC_CD , PRR_CND , INP_VAL , TST_DES , OUP_VAL , TP , OUP_MENS , PGM_ID , SCRN_ID , TRN_CD , RQU_SBH_ID , TST_RST , TST_ACHI_RST , RMRK , ATFL_MNG_ID , OPR_NO , DB_CHG_TS FROM pms_itg_tst_mng
WHERE 1=1 /*차수구분*/
AND SQN_CD = #{sqn_cd}
/*처리단계*/
AND ITG_TST_PRC_CD = #{itg_tst_prc_cd}
/*유형*/
AND TP = #{tp}
/*시나리오ID*/
AND SCNR_ID = '%' #{scnr_id} '%'
/*테스트케이스ID*/
AND TST_CASE_NM = '%' #{tst_case_nm} '%'
/*개발자명*/
AND DVLPE_ENO = '%' #{dvlpe_eno} '%'
/*담당PL번호*/
AND PL_ENO = '%' #{pl_eno} '%'
/*담당현업번호*/
AND CRPE_ENO = '%' #{crpe_eno} '%'
/*요구사항ID*/
AND RQU_SBH_ID = '%' #{rqu_sbh_id} '%'
/*예상종료일자*/
AND FRCS_END_DT BETWEEN #{frcs_end_dt01} AND #{frcs_end_dt02}
/*개발자확인일자*/
AND DVLPE_CNF_DT BETWEEN #{dvlpe_cnf_dt01} AND #{dvlpe_cnf_dt02}
