mapper.select:

           SELECT BKUP_ID                  /*백업ID */
                , PRJT_ID                  /*프로젝트ID  */
                , MNG_ID                   /*관리ID  */
                , RGS_DIS_CD               /*관리구분코드 */
                , TITL_NM                  /*제목  */
                , REQ_DIS_TXT              /*요청내용 */
                , REQ_DIS_CD               /*요청구분코드*/
                , RGS_DT                   /*요청일자*/
                , ACHI_NM                  /*요청자 */
                , PRC_STEP_CD              /*이슈처리단계구분코드 */
                , TGT_BIZ_NM               /*대상업무명 */
                , TTMN_CRPE_NM             /*조치담당자 */
                , TTMN_SCD_DT              /*조치예정일자*/
                , TTMN_DT                  /*조치일자*/
                , TTMN_TXT                 /*조치내용*/
                , IFNC_CD                  /*영향도코드*/
                , GD_TXT                   /*등급내용*/
                , URGN_CD                  /*긴급코드*/
                , RMRK                     /*비고*/
                , OPR_NO                   /*조작자번호*/
                , DB_CHG_TS                /*DB변경일시*/
            FROM pms_iss_risk_mng

            WHERE 1=1
	   		 /*관리구분*/

	   		   AND RGS_DIS_CD = #{rgs_dis_cd}

	   		 /*요청구분*/

	   		   AND REQ_DIS_CD = #{req_dis_cd}

	   		 /*처리상태*/

	   		   AND PRC_STEP_CD = #{prc_step_cd}

			  /*조치업무명*/

				  AND TGT_BIZ_NM = '%' #{tgt_biz_nm} '%'

			  /*요청자*/

				  AND ACHI_NM = '%' #{achi_nm} '%'

			  /*조치담당자*/

				  AND TTMN_CRPE_NM = '%' #{ttmn_crpe_nm} '%'

			  /*제목*/

				  AND TITL_NM = '%' #{titl_nm} '%'

			  /*요청내용*/

				  AND REQ_DIS_TXT = '%' #{req_dis_txt} '%'

			  /*조치내용*/

				  AND TTMN_TXT = '%' #{ttmn_txt} '%'

			  /*해결방안내용*/

				  AND SLV_MPLN_TXT = '%' #{slv_mpln_txt} '%'
